function getTitle(html) {
  const m = html.match(/<span\s+itemprop="title">([^<]+)<\/span>/);
  return m ? m[1].trim() : null;
}

function getDate(html) {
  const m = html.match(
    /(?:Actualis√©|Publi√©) le\s+(\d{1,2}\s+[A-Za-z√Ä-√ø]+\s+\d{4})/i
  );
  return m && m[1] ? m[1] : null;
}

function toIsoDate(frDate) {
  if (!frDate) return null;
  // ex. "07 mai 2025"
  const parts = frDate.split(' ');
  const j = parts[0].padStart(2,'0');
  const moisMap = {
    janvier: '01', f√©vrier: '02', mars: '03', avril: '04',
    mai: '05', juin: '06', juillet: '07', ao√ªt: '08',
    septembre: '09', octobre: '10', novembre: '11', d√©cembre: '12'
  };
  const m = moisMap[parts[1].toLowerCase()];
  const a = parts[2];
  return m ? `${a}-${m}-${j}` : null;
}

function getCompany(html) {
  // 1) Sch√©ma Organization
  let m = html.match(
    /<span\s+itemtype="http:\/\/schema\.org\/Organization"[\s\S]*?<span[^>]*itemprop="name"[^>]*content="([^"]+)"/i
  );
  if (m && m[1]) return m[1].trim();

  // 2) Section Employeur
  m = html.match(
    /<h2 class="t5 subtitle">\s*Employeur\s*<\/h2>[\s\S]*?<h3 class="t4 title">\s*([^<]+)\s*<\/h3>/i
  );
  if (m && m[1]) return m[1].trim();

  return null;
}

function getLocation(html) {
  const m = html.match(/<span\s+class="location">([^<]+)<\/span>/);
  return m ? m[1].trim() : null;
}

function getDescription(html) {
  const m = html.match(
    /<div\s+itemprop="description"[^>]*>([\s\S]*?)<\/div>/
  );
  return m
    ? m[1].replace(/<[^>]+>/g, '').trim()
    : null;
}

function getJobId(html) {
  const m = html.match(/Offre n¬∞\s*<span[^>]*>(\d+)<\/span>/);
  return m ? m[1] : null;
}

