const outputs = []; // Array to store parsed job offers
let finished = false; // Flag to stop parsing once general info is reached

for (const item of $input.all()) {
  if (finished) break;

  const htmlContent = item.json.textAsHtml || '';

  // Split HTML content into blocks using sequences of '-' or '_'
  // The <p> tags are optional
  const offerBlocks = htmlContent
    .split(/(?:<p>)?[-_]{3,}(?:<\/p>)/gi)
    .map(block => block.trim())
    .filter(block => block.length > 0);

  for (const block of offerBlocks) {
    if (finished) break;

    console.log({ block })

    // Count the number of <p> tags in the block
    const pCount = (block.match(/<p>/gi) || []).length;

    // Process only blocks containing at least 3 <p> paragraphs
    if (pCount > 2) {
      let title = null;
      let entreprise = null;
      let departement = null;
      let city = null;
      let contract = null;
      let fulltime = null;
      let description = [];
      let ref = null
      let link = null

      // Get ref from block
      const refMatch = block.match(/num(?:√©|e|&eacute;)ro\s*(\d+)/i);
      if (refMatch) {
        ref = refMatch[1];
      }

      if (ref) link = `https://candidat.francetravail.fr/offres/recherche/detail/${ref}`

      // Extract each <p> content one by one
      const regex = /<p>(.*?)<\/p>/gi;
      let match;
      let i = 0;
      while ((match = regex.exec(block)) !== null) {
        if (finished) break;

        const content = match[1].trim();

        if (i === 0) {
          // First paragraph ‚Üí title
          title = content;
        } else if (i === 1) {
          // Second paragraph ‚Üí entreprise - departement - city
          const parts = content.split('-');
          if (parts.length >= 3) {
            entreprise = parts[0].trim();
            departement = parts[1].trim();
            city = parts[2].trim();
          } else {
            entreprise = content;
          }
        } else if (i === 2) {
          // Third paragraph ‚Üí contract - fulltime
          const parts = content.split('-');
          if (parts.length >= 2) {
            contract = parts[0].trim();
            fulltime = parts[1].trim();
          } else {
            contract = content;
          }
        } else {
          // From the fourth paragraph onwards ‚Üí description
          if (content.includes("rendez-vous sur le site")) {
            finished = true;
          }
          if (!finished) {
            description.push(content);
          }
        }

        i++;
      }

      // Save the parsed offer
      outputs.push({
        title,
        entreprise,
        departement,
        city,
        contract,
        fulltime,
        ref,
        link,
        description: description.join(' '), // Concatenate all description parts
      });
    }
  }
}

return outputs;
